document.addEventListener("DOMContentLoaded",(function(){fetch("top-container.html").then((function(t){if(!t.ok)throw new Error("Failed to load top container");return t.text()})).then((function(t){var n=document.getElementById("top-container-placeholder");n&&(n.innerHTML=t)})).catch((function(t){})),fetch("footer.html").then((function(t){if(!t.ok)throw new Error("Failed to load footer");return t.text()})).then((function(t){var n=document.getElementById("footer-placeholder");n&&(n.innerHTML=t)})).catch((function(t){}))}));